package Operator;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 13;
		int num2 = 9;
		System.out.println(num1&num2);
		System.out.println(num1|num2);
		System.out.println(num1^num2);
		System.out.println(num1>>num2);
		System.out.println(num1<<num2);
	}

}
